import React from 'react'
import Carousel from 'react-bootstrap/Carousel';
import './MySlider.css'
const MySlider = ({children}) => {
  return (
    <Carousel>

      {children.map((item) => (
        //   <SliderItem img={item.img} title={item.title} description={item.description}/>
        <Carousel.Item interval= {100000} className='carousel__item'>
        <img
          className="d-block w-100 carousel__img"
          src={item.img}
          alt={item.title}
        />
        <Carousel.Caption>
          <h3>{item.title}</h3>
          <p>{item.description}</p>
        </Carousel.Caption>
      </Carousel.Item>
        ))}
    </Carousel>
  )
}

export const SliderItem = ({img, title, description}) => (
    <Carousel.Item>
        <img
          className="d-block w-100"
          src={img}
          alt={title}
        />
        <Carousel.Caption>
          <h3>{title}</h3>
          <p>{description}</p>
        </Carousel.Caption>
      </Carousel.Item>
)

export default MySlider
